contracts/DisputeLogic.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract DisputeLogic {
    enum DisputeStatus { None, Raised, Escalated, Resolved }

    struct Dispute {
        address raisedBy;
        string reason;
        DisputeStatus status;
        uint256 raisedAt;
    }

    mapping(uint256 => Dispute) public disputes;

    event DisputeRaised(uint256 indexed taskId, address indexed by, string reason);
    event DisputeEscalated(uint256 indexed taskId);
    event DisputeResolved(uint256 indexed taskId, string resolution);

    modifier onlyInDispute(uint256 taskId) {
        require(disputes[taskId].status != DisputeStatus.None, "No dispute found");
        _;
    }

    function raiseDispute(uint256 taskId, string calldata reason) external {
        require(disputes[taskId].status == DisputeStatus.None, "Dispute already exists");

        disputes[taskId] = Dispute({
            raisedBy: msg.sender,
            reason: reason,
            status: DisputeStatus.Raised,
            raisedAt: block.timestamp
        });

        emit DisputeRaised(taskId, msg.sender, reason);
    }

    function escalateDispute(uint256 taskId) external onlyInDispute(taskId) {
        require(disputes[taskId].status == DisputeStatus.Raised, "Not eligible to escalate");
        disputes[taskId].status = DisputeStatus.Escalated;
        emit DisputeEscalated(taskId);
    }

    function resolveDispute(uint256 taskId, string calldata resolution) external onlyInDispute(taskId) {
        disputes[taskId].status = DisputeStatus.Resolved;
        emit DisputeResolved(taskId, resolution);
    }

    function getDispute(uint256 taskId) external view returns (
        address raisedBy,
        string memory reason,
        DisputeStatus status,
        uint256 raisedAt
    ) {
        Dispute storage d = disputes[taskId];
        return (d.raisedBy, d.reason, d.status, d.raisedAt);
    }
}

contracts/Escrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IUSDT {
    function transferFrom(address sender, address recipient, uint amount) external returns (bool);
    function transfer(address recipient, uint amount) external returns (bool);
}

contract Escrow {
    address public owner;
    IUSDT public usdt;

    struct Task {
        string details;
        uint256 reward;
        address poster;
        bool isActive;
    }

    Task[] public tasks;

    constructor(address _usdtAddress) {
        owner = msg.sender;
        usdt = IUSDT(_usdtAddress);
    }

    function postTask(string memory _details, uint256 _reward) external {
        require(usdt.transferFrom(msg.sender, address(this), _reward), "USDT transfer failed");

        tasks.push(Task({
            details: _details,
            reward: _reward,
            poster: msg.sender,
            isActive: true
        }));
    }

    function getAllTasks() external view returns (Task[] memory) {
        return tasks;
    }

    function cancelTask(uint taskId) external {
        Task storage task = tasks[taskId];
        require(msg.sender == task.poster, "Not poster");
        require(task.isActive, "Already inactive");

        task.isActive = false;
        require(usdt.transfer(msg.sender, task.reward), "Refund failed");
    }
}

contracts/InstantGigs.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
}

contract InstantGigs {
    struct Gig {
        address seller;
        string title;
        string description;
        uint256 price;
        bool active;
    }

    uint256 public gigCounter;
    address public USDT;

    mapping(uint256 => Gig) public gigs;
    mapping(uint256 => address) public buyers;
    mapping(address => uint256[]) public gigsBySeller;

    event GigCreated(uint256 indexed gigId, address indexed seller, string title, uint256 price);
    event GigPurchased(uint256 indexed gigId, address indexed buyer);

    constructor(address _usdt) {
        USDT = _usdt;
    }

    function createGig(string calldata title, string calldata description, uint256 price) external {
        gigCounter++;
        gigs[gigCounter] = Gig({
            seller: msg.sender,
            title: title,
            description: description,
            price: price,
            active: true
        });
        gigsBySeller[msg.sender].push(gigCounter);

        emit GigCreated(gigCounter, msg.sender, title, price);
    }

    function purchaseGig(uint256 gigId) external {
        Gig storage gig = gigs[gigId];
        require(gig.active, "Gig not active");

        IERC20(USDT).transferFrom(msg.sender, gig.seller, gig.price);
        buyers[gigId] = msg.sender;

        emit GigPurchased(gigId, msg.sender);
    }

    function getSellerGigs(address seller) external view returns (uint256[] memory) {
        return gigsBySeller[seller];
    }

    function deactivateGig(uint256 gigId) external {
        require(gigs[gigId].seller == msg.sender, "Not seller");
        gigs[gigId].active = false;
    }
}

